function HttpProvider(url = values.rpc.url, opts = {}) {
  this.url = url
  this.opts = opts
}

HttpProvider.prototype.send = function(payload, done) {
  let body = JSON.stringify(payload)
  let url = this.url
  let fetchOpts = Object.assign({}, this.opts, {
    method,
    headers,
    body
  })

  fetch(url, fetchOpts)
    .then(res => res.json())
    .then(res => done(null, res))
    .catch(done)
}

