let randomHex = size => randomhex(size)

/**
 *
 * @member _
 * @type {function}
 */
// utils._ = _

/**
 * Constructor for bn.js
 *
 * It would be the same as `require('bn.js')`
 *
 * @constructor BN
 * @member BN
 */
// utils.BN = BN

