/**
 * Get the constructor of whatever is passed in
 * @access private
 * @param {object} obj
 * @returns {string} constructor name
 */
let getConstructor = val => get(val, 'constructor.name')

/**
 * BN.isBN
 * @param {object} val
 * @returns {boolean}
 */
let isBN = val => BN.isBN(val)

/**
 * Is it a `BigNumber` or not?
 * @param {object} obj
 * @return {boolean}
