 * Convert array of bytes into hex string with 0x prepended
 * @param {array} val bytes
 * @return {string}
 */
let bytesToHex = val =>
  prependZeroX(
    val
      .map(item => {
        let op = item.toString(16)
        if (op.length === 1) {
          // zero pad
          op = `0${op}`
        }
        return op
      })
      .join('')
  )

