    throw new Error(`not a valid aion address: ${val}`)
  }

  return accounts.isValidChecksumAddress(val)
}

function utf8ToHex(val) {
  let op = utf8.encode(val)
  let hex = ''
  let code
  let codeString

  op = op.replace(patterns.utf8Null, '')
  op = op
    .split('')
    .reverse()
    .join('')
  op = op.replace(patterns.utf8Null, '')
  op = op
    .split('')
    .reverse()
    .join('')

  for (let i = 0; i < op.length; i++) {
    code = op.charCodeAt(i)
    codeString = code.toString(16)
    hex += codeString.length < 2 ? '0' + codeString : codeString
  }

  return '0x' + hex
}

function numberToHex(val) {
  if (val === undefined || val === null) {
    // should this warn? if the user thinks its a number but its not
    // it could cause other problems
    // console.warn('numberToHex, undefined or null', val)
    return val
  }

  if (isFinite(val) === false && isHexStrict(val) === false) {
    throw new Error('numberToHex expecting number and got something else')
  }

  let number = numberToBn(val)
  let result = number.toString(16)
  return number.lt(new BN(0)) ? '-0x' + result.substring(1) : '0x' + result
}

/**
 * It's used internally to convert addresses, booleans, objects, strings,
 * and numbers into hex for solidity sha3.
 * @param {object} val
 * @param {string} [returnType]
 * @return {string}
 */
function toHex(val, returnType) {
  let {maxIntSize, hex, zeroX} = values
  let {zero, one} = hex

  if (isAddress(val) === true) {
    return returnType
      ? 'address'
      : zeroX + removeLeadingZeroX(val.toLowerCase())
  }

  if (isBoolean(val) === true) {
    return returnType ? 'bool' : val ? one : zero
  }

  if (
    isObject(val) === true &&
    isBigNumber(val) === false &&
    isBN(val) === false
  ) {
    return returnType ? 'string' : utf8ToHex(JSON.stringify(val))
  }

  // if its a negative number, pass it through numberToHex
  if (isString(val) === true) {
    if (patterns.zeroXNegative.test(val) === true) {
      return returnType ? `int${maxIntSize}` : numberToHex(val)
    }

    if (startsWithZeroX(val) === true) {
      return returnType ? 'bytes' : val
    }

    if (isFinite(val) === false) {
      return returnType ? 'string' : utf8ToHex(val)
    }
  }

  if (isEmpty(returnType) === false) {
    if (val < 0) {
      return `int${maxIntSize}`
    }

    return `uint${maxIntSize}`
  }

  return numberToHex(val)
}

function elementaryName(name) {
  if (name.startsWith('int[') === true) {
    // for example appends '[45]'
    return 'int128' + name.slice(3)
  }
  if (name === 'int') {
    return 'int128'
  }
  if (name.startsWith('uint[') === true) {
    return 'uint128' + name.slice(4)
  }
  if (name === 'uint') {
    return 'uint128'
  }
  if (name.startsWith('fixed[') === true) {
    return 'fixed128x128' + name.slice(5)
  }
  if (name === 'fixed') {
    return 'fixed128x128'
  }
  if (name.startsWith('ufixed[') === true) {
    return 'ufixed128x128' + name.slice(6)
  }
  if (name === 'ufixed') {
    return 'ufixed128x128'
  }
  return name
}

function parseNumber(val) {
  let type = typeof val
  if (type === 'string') {
    if (isHexStrict(val) === true) {
      return new BN(removeLeadingZeroX(val), 16)
    }
    return new BN(val, 10)
  }
  if (type === 'number') {
    return new BN(val)
  }
  if (isBigNumber(val) === true) {
    return new BN(val.toString(10))
  }
  if (isBN(val) === true) {
    return val
  }
  throw new Error(`unable to parse type "${type}"`)
}

// Parse N from type<N>
function parseTypeN(type) {
  let typesize = patterns.typeN.exec(type)
  return typesize ? parseInt(typesize[1], 10) : null
}

function parseTypeNArray(type) {
  let arraySize = patterns.typeNArray.exec(type)
  return arraySize ? parseInt(arraySize[1], 10) : null
}

function solidityPack(type, value, arraySize) {
  let size
  let num
  let {maxIntSize} = values
  let {zero, one} = values.solidity.pack
  let padAmount
  type = elementaryName(type)

  if (type === 'bytes') {
    if (removeLeadingZeroX(value).length % 2 !== 0) {
      throw new Error('Invalid bytes characters ' + value.length)
    }

    return value
  }

  if (type === 'string') {
    return utf8ToHex(value)
  }

  if (type === 'bool') {
    return value ? one : zero
  }

  if (type.startsWith('address') === true) {
    size = values.addressLength

    if (isAddress(value) === false) {
      throw new Error(`"${value}" is not a valid address`)
    }

    return leftPad(value.toLowerCase(), size)
  }

  size = parseTypeN(type)

  if (type.startsWith('bytes') === true) {
    if (size === undefined) {
      throw new Error('bytes[] not yet supported in solidity')
    }

    // must be 32 byte slices when in an array
    if (isNumber(arraySize) === true) {
      size = 32
    }

    if (size < 1 || size > 32 || size < removeLeadingZeroX(value).length / 2) {
      throw new Error(`Invalid bytes${size} for ${value}`)
    }

    return rightPad(value, size * 2)
  }

  if (type.startsWith('uint') === true) {
    if (size % 8 || size < 8 || size > maxIntSize) {
      throw new Error(`invalid uint size: ${size}, max size: ${maxIntSize}`)
    }

    num = parseNumber(value)
    if (num.bitLength() > size) {
      throw new Error(
        'Supplied uint exceeds width: ' + size + ' vs ' + num.bitLength()
      )
    }

    if (num.lt(new BN(0)) === true) {
      throw new Error('Supplied uint ' + num.toString() + ' is negative')
    }

    // TODO: figure out these values
    padAmount = size / 8
    padAmount = padAmount * 2
    return size ? leftPad(num.toString('hex'), padAmount) : num
  }

  if (type.startsWith('int') === true) {
    if (size % 8 || size < 8 || size > maxIntSize) {
      throw new Error('Invalid int' + size + ' size')
    }

    num = parseNumber(value)
    if (num.bitLength() > size) {
      throw new Error(
        'Supplied int exceeds width: ' + size + ' vs ' + num.bitLength()
      )
    }

    if (num.lt(new BN(0)) === true) {
      return num.toTwos(size).toString('hex')
    }

    // TODO: figure out these values
    padAmount = size / 8
    padAmount = padAmount * 2
    return size ? leftPad(num.toString('hex'), padAmount) : num
  }

  // FIXME: support all other types
  throw new Error('Unsupported or invalid type: ' + type)
}

function soliditySortArg(item) {
  let type
  let value
  let arraySize
  let hexArg

  if (isArray(item) === true) {
    throw new Error(
      `soliditySha3 doesn't accept Array type because ethereum web3 doesn't`
    )
  }

  // { t: '', v: ''}
  // { type: '', value: ''}
  if (
    typeof item === 'object' &&
    (item.t !== undefined || item.type !== undefined) &&
    (item.v !== undefined || item.value !== undefined)
  ) {
    type = item.type || item.t
    value = item.value || item.v
  } else {
    type = toHex(item, true)
    value = toHex(item)

    if (type.startsWith('int') === false && type.startsWith('uint') === false) {
      type = 'bytes'
    }
  }

  if (
    (type.startsWith('int') === true || type.startsWith('uint') === true) &&
    typeof value === 'string' &&
    patterns.zeroXNegative.test(value) === false
  ) {
    value = new BN(value)
  }

  // get the array size
  if (isArray(value) === true) {
    arraySize = parseTypeNArray(type)
    if (isNumber(arraySize) === true && value.length !== arraySize) {
      let arr = JSON.stringify(value)
      throw new Error(`${type} is not matching the given array ${arr}`)
    } else {
      arraySize = value.length
    }
  }

  if (isArray(value) === true) {
    hexArg = value.map(item => {
      return removeLeadingZeroX(
        solidityPack(type, item, arraySize).toString('hex')
      )
    })
    return hexArg.join('')
  } else {
    hexArg = solidityPack(type, value, arraySize)
    return removeLeadingZeroX(hexArg.toString('hex'))
  }
}

/**
 * Converts all the arguments into some other format then hashes it with sha3.
 * @param args captures all arguments
 * @return {string} [description]
 */
function soliditySha3() {
  return sha3(
    prependZeroX(
      Array.from(arguments)
        .map(soliditySortArg)
        .join('')
    )
  )
}

/**
 * Convert an Aion address to an Aion Checksum address.
 *
 * Rather than being all lower or uppercase letters are upper or lower based
 * on some critera.
 *
 * @param {string} val
 * @return {string}
 */
let toChecksumAddress = val => {
  if (accounts.isAccountAddress(val) === false) {
    // this differs from how ethereum web3 does it
    // ethereum web3 returns a blank string
    // we throw if it's invalid
    throw new Error(`Given address "${val}" is not a valid Aion address.`)
  }

  return accounts.createChecksumAddress(val)
}

/**
 * Convert hex string to number string
 * @param {string} val
 * @return {string}
 */
let hexToNumberString = val => toBN(val).toString(10)

/**
 * Convert hex string to javascript number
 * @param {string} val
 * @return {number}
 */
let hexToNumber = val => toBN(val).toNumber()

/**
 * Convert hex string to utf8 string
 * @param {string} val
 * @return {string}
 */
function hexToUtf8(val) {
  // it removes 000000 padding
  let op = removeLeadingZeroX(val)
    .replace(patterns.leadingHexZeroPadding, '')
    .split('')
    .reverse()
    .join('')
    .replace(patterns.leadingHexZeroPadding, '')
    .split('')
    .reverse()
    .join('')

  op = prependZeroX(op)
  op = hexToBytes(op)
    .map(item => String.fromCharCode(item))
    .join('')

  return utf8.decode(op)
}

function hexToAscii(val) {
  return hexToBytes(val)
    .map(item => String.fromCharCode(item))
    .join('')
}

function asciiToHex(val) {
  if (isEmpty(val) === true) {
    return copyString(values.hex.zero)
  }

  return prependZeroX(
    val
      .split('')
      .map(item => item.charCodeAt(0).toString(16))
      .join('')
  )
}

let toTwosComplement = val =>
  prependZeroX(
    toBN(val)
      .toTwos(256)
      .toString(16, 64)
  )

function blake2b256(val) {
  if (
    val === undefined ||
    val === null ||
    // not having slice meaning it's not an array-like thing
    (typeof val !== 'string' &&
      get(val, 'slice') === undefined &&
      isBN(val) === false)
  ) {
    throw new Error(`blake2b256 invalid value: "${val}"`)
  }

  let op

  if (typeof val === 'string') {
    // copy the string
    op = copyString(val)

    if (startsWithZeroX(op) === true) {
      // the user passed in a string like '0xdccd2e7f400ab38e'
      op = hexToBytes(op)
    } else {
      op = Buffer.from(op)
    }
  }

  if (isBN(val) === true) {
    op = Buffer.from(val.toString())
  }

  op = crypto.blake2b256(op)
  op = op.toString('hex')

  return `0x${op}`
}

module.exports = {
  // ethereum web3 implementations
  randomHex,
  _,
  BN,
  toBN,
  isBN,
  isBigNumber,
  isHex,
  isHexStrict,
  hexToBytes,
  bytesToHex,
  toHex,
  checkAddressChecksum,
  toChecksumAddress,
  utf8ToHex,
  isAddress,
  hexToNumberString,
  hexToNumber,
  numberToHex,
  hexToUtf8,
  hexToAscii,
  asciiToHex,
  padLeft,
  leftPad,
  padRight,
  rightPad,
  toTwosComplement,
  sha3,
  keccak256: sha3,
  soliditySha3,
  blake2b256
}

let {isString, isEmpty, each} = require('underscore')
let {isBuffer, toBuffer} = require('./lib/formats')
let {isAccountAddress} = require('./lib/accounts')

let nonAccountKeys = ['_accounts', 'length', 'defaultKeyName']

function Wallet(accounts) {
  this._accounts = accounts
  this.length = 0
  this.defaultKeyName = 'web3js_wallet'
}

Wallet.prototype._recalculateLength = function() {
  this.length = Object.keys(this).length - nonAccountKeys.length
}

Wallet.prototype._hasAddress = function(address) {
  return (
    Object.keys(this)
      .map(item => item.toLowerCase())
      .indexOf(address.toLowerCase()) > -1
  )
}

Wallet.prototype.add = function(val) {
  let account = val
  if (isString(val) === true || isBuffer(val) === true) {
    account = this._accounts.privateKeyToAccount(toBuffer(val))
  }
  this[account.address] = account
  this._recalculateLength()
  return account
}

Wallet.prototype.create = function(numberOfAccounts, entropy) {
  for (let i = 0; i < numberOfAccounts; ++i) {
    this.add(this._accounts.create(entropy))
  }
  return this
}

Wallet.prototype.remove = function(address) {
  let exists = this._hasAddress(address)
  delete this[address]
  this._recalculateLength()
  return exists
}

Wallet.prototype.clear = function() {
  let wallet = this
  Object.keys(this).forEach(key => {
    if (isAccountAddress(key) === true) {
      delete wallet[key]
    }
  })
  wallet._recalculateLength()
  return this
}

Wallet.prototype._getPrivateKeys = function() {
  let items = []
  each(this, (val, key) => {
    if (isAccountAddress(key) === true && isEmpty(val.privateKey) === false) {
      items.push(val.privateKey)
    }
  })
  return items
}

Wallet.prototype.encrypt = function(password, options) {
  return this._getPrivateKeys().map(privateKey =>
    this._accounts.encrypt(privateKey, password, options)
  )
}

Wallet.prototype.decrypt = function(keystores, password) {
  each(keystores, keystore => {
    let account = this._accounts.decrypt(keystore, password)
    this.add(account)
  })
  return this
}

Wallet.prototype.save = function(password, keyName) {
  if (typeof window === 'undefined') {
    throw new Error('wallet save is a browser local storage function')
  }
  let op = this.encrypt(password, {kdf: 'pbkdf2'})
  op = JSON.stringify(op)
  window.localStorage.setItem(keyName || this.defaultKeyName, op)
  return true
}

Wallet.prototype.load = function(password, keyName) {
  if (typeof window === 'undefined') {
    throw new Error('wallet load is a browser local storage function')
  }
  let keystoresText = window.localStorage.getItem(
    keyName || this.defaultKeyName
  )
  let keystores = JSON.parse(keystoresText)
  this.decrypt(keystores, password)
  return this
}

module.exports = Wallet

/*

Developer notes:
If it looks complicated it's because it's trying to intelligently
connect, reconnect, and send messages but there's no request-response
paradigm. It relies on sending an `id` then watching messages that come back
with that `id`.

It uses console due to there not yet being an interface for giving
feedback to the API consumer.

*/

let WebSocket = require('ws/lib/websocket')

function WebsocketProvider(url, opts) {
  let prov = {}
  let client
  let handlers = {}
  let connectTries = 0
  let connectTriesMax = 10

  let connect = () => {
    connectTries += 1

    if (connectTries > connectTriesMax) {
      /* eslint-disable no-console */
      console.error('aion web3 websocket provider')
      /* eslint-enable no-console */
    }

    client = new WebSocket(url, opts)

    client.on('message', res => {
      let body = JSON.parse(res)
      if (body.id !== undefined && handlers[body.id] !== undefined) {
        handlers[body.id](body)
      }
    })

    client.on('error', err => {
      /* eslint-disable no-console */
      console.error('websocket provider error', err)
      /* eslint-enable no-console */
    })
  }

  prov.send = (payload, done) => {
    let sendTries = 0
    let sendTriesMax = 10
    let sent = false
    let finished = false
    let tryTimer

    // random message id so we know how to get our response
    let id = Math.random()
      .toString()
      .substring(2)

    let body = JSON.stringify(Object.assign({}, payload, {id}))

    let cleanup = () => {
      finished = true
      clearInterval(tryTimer)
      delete handlers[id]
    }

    // message response handler
    handlers[id] = body => {
      if (finished === true) {
        return
      }
      done(null, body)
      cleanup()
    }

    if (client === undefined) {
      connect()
    }

    if (client.readyState === 1) {
      // already connected, just send
      client.send(body)
      return
    }

    let retry = () => {
      sendTries += 1

      if (sent === true || finished === true) {
        // already finished, do nothing
        return
      }

      if (sendTries > sendTriesMax) {
        // too many
        sent = true
        done(
          new Error(`
            WebSocket send failed,
            Send tries:  ${sendTries},
            Max send tries: ${sendTriesMax}
          `)
        )
        cleanup()
        return
      }

      if (client.readyState === 0) {
        // waiting to connect
        return
      }

      if (client.readyState === 1) {
        // connected again, send!
        client.send(body)
        sent = true
        return
      }

      if (client.readyState === 2) {
        // disconnecting
        return
      }

      if (client.readyState === 3) {
        // disconnected, reconnect
        connect()
        return
      }
    }

    tryTimer = setInterval(retry, 50)
  }

  return prov
}

module.exports = WebsocketProvider
